let count=0;const value=document.querySelector("#value"),btns=document.querySelectorAll(".btn");btns.forEach((function(n){n.addEventListener("click",(n=>{const t=n.currentTarget.classList;t.contains("decrease")?count--:t.contains("increase")?count++:t.contains("double")?count*=2:t.contains("undouble")?count/=2:count=0,value.style.color=count>0?"green":count<0?"red":"#222",value.textContent=count}))}));